css:
.product__buy button {
color: #ffffff;
background-color: #25BA34;
border-color: #25BA34;
width: 100%;
}
.product__buy button:hover {
background: #1B8726;
border-color: #1B8726;
}
.product__sticky-buy .btn-primary{
background-color: #25BA34;
border-color: #25BA34;
}

.product__sticky-buy .btn-primary:hover{
background-color: #1B8726;
border-color: #1B8726;
}
.product__short-stock {
display: none !important;
}
.cookieconsent {
  background-color: #373737e8;
}
.cookieconsent .text::before {
  content: 'Cookies: a gente guarda estatísticas de visitas para melhorar sua experiência de navegação, saiba mais em nossa ';
  font-size: 13px;
}
.cookieconsent .text {
  color: #fff;
  font-size: 0px;
}
.cookieconsent .text a {
  font-size: 13px;
  color: #FCD000;
}

.cookieconsent button {
  color: #fff;
  border: 1px solid #fff;
}

@media (max-width: 991.98px) {
  .cookieconsent {
    top: 90px;
    bottom: auto;
  }
}
